function changeTab(arg,tabName){'use strict';var i,tabcontent,tab;tabcontent=document.getElementsByClassName("tabcontent");for(i=0;i<tabcontent.length;i++){tabcontent[i].style.display="none"}
tab=document.getElementsByClassName("tab");for(i=0;i<tab.length;i++){tab[i].className=tab[i].className.replace(" active","")}
document.getElementById(tabName).style.display="block";arg.currentTarget.className+=" active"}
